package Day4;

public class Printweeknumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int weekno=3;
		
		if(weekno==1)
		{
			System.out.println("Monday");
		}
		else if(weekno==2)
		{
			System.out.println("Tuesday");
			
		}
		else if(weekno==3)
		{
			System.out.println("Wednesday");
		}
		
	}

}
